/*
* rdate.c  client program for remote date program
*/
#include <stdio.h>
#include <rpc/rpc.h> /* standard RPC include file */
#include "strs.h" /* this file is generated by rpcgen */

int main(int argc, char *argv[])
{
    CLIENT *cl; /* RPC handle */
    char *server;
    char **sresult; /* return value from str_date_1() */
    int i, num_strs;
    input_data * data;

    if (argc < 2)
    {
        fprintf(stderr, "usage: %s hostname <strings>\n", argv[0]);
        exit(1);
    }
    server = argv[1];
    /*
    * Create client handle
    */
    if ((cl = clnt_create(server, STRS_PROG, STRS_VERS, "tcp")) == NULL)
    {
        /*
        * canâ€™t establish connection with server
        */
        clnt_pcreateerror(server);
        exit(2);
    }

    num_strs = argc - 2;
    data->num_strs = num_strs;

    if (num_strs > 0)
      data->argv0 = argv[2];
    if (num_strs > 1)
      data->argv1 = argv[3];
    if (num_strs > 2)
      data->argv2 = argv[4];
    if (num_strs > 3)
      data->argv3 = argv[5];

    if ( (sresult = upper_1(data, cl)) == NULL)
    {
        clnt_perror(cl, server);
        exit(3);
    }
    printf("%s\n", *sresult);

    clnt_destroy(cl); /* done with the handle */
    exit(0);
}
